sort=[];
push=(title,url,type)=> sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: 1,
				layout_flexBasisPercent: type
			}
	});
$$=(a,b,c)=> `${a}_${b}_${c}_{{page}\}.htm`;

a="weekvisit", b=c=0;
d=(title,a,b,c)=> push("༺ˇ»`ʚ "+title+" ɞ´«ˇ༻",$$(a,b,c),1);

arra=[["人气","weekvisit"],["推荐","allvote"],["新书","newhot"]];
arrb=["综合全部","玄幻魔法","修真武侠","言情小说","历史军事","游戏竞技","科幻空间","悬疑惊悚","同人小说","都市小说","官场职场","穿越时空","青春校园","其他小说"];
arrc=(title,a,b)=>["["+title+"]","全本","连载"]
		.map((title,c)=>push(title,$$(a,b,c),0.25));


if(style==0){
		arrb.map((title,b)=>{
				d(title,a,b,c);
				arra.map([title,a]=>arrc(title,a,b));
			});
	}
else{
		arra.map([title,a]=>{
				d(title,a,b,c);
				arrb.map((title,b)=>arrc(title,a,b));
			});
	}
JSON.stringify(sort);

