{{source.bookSourceComment}}

//调试标志位 只显示第一张图片
let DEBUG = 0;

//本地密钥
local_keys=null;

function base64Decode(base64) {
    return java.base64Decode(base64) + ""
}
//aes解密 多个密钥尝试
let aes = {
  keys: null,
  retryNum: 2,
  saveKeys: function(keysString) {
    try {
      this.keys = JSON.parse(keysString);
      let localKeys = source.getVariable();
      if (localKeys != keysString) {
        source.setVariable(keysString);
        return true
      }
    } catch (e) {
       java.log(keysString);
       java.log("密钥解析错误");
    }
  },
  //java实现aes解密 速度快一些
  _aesDecrypt: function(key, word) {
    return java.createSymmetricCrypto("aes/ecb/pkcs7padding", key).decryptStr(word) + ""
  },
  decrypt: function(word, name) {
    if (this.keys && this.keys[name]) {
      let decrypted;
      for (let key of this.keys[name]) {
        try {
          decrypted = this._aesDecrypt(key, word);
        } catch {}
        if (!decrypted) continue
        return decrypted
      }
    }
    if ((this.retryNum--) > 0) {
    	  if ((this.retryNum == 2 &&
    	  	  this.saveKeys(fetchRemoteKeys())) ||
    	  	  (this.retryNum == 1 &&
    	  	  	this.saveKeys(parseKeys()))
    	  ) {
    	  java.log(">>> 自动重试...");
    	  return this.decrypt(word, name)
    	  }
    }
    throw`❎${name}解密失败，尝试订阅源解密`;
  }
}
function decodeImage(src) {
  let C_DATA = src.match(/C_DATA\s*=\s*\S([\s\S]+?)['"]/);
  if (!C_DATA) throw "图片数据C_DATA获取失败！！";
  C_DATA = aes.decrypt(base64Decode(C_DATA[1]), "C_DATA");
  DEBUG && java.log(C_DATA);
  eval(C_DATA);
  let html = ""
  if (!image_info.img_type) {
    //图片路径 和总数
    let path, totalImageCount;
    if (typeof mh_info.enc_code2 == "undefined") {
      path = mh_info.imgpath;
    } else {
      path = aes.decrypt(base64Decode(mh_info.enc_code2), "enc_code2");
    }
    if (typeof mh_info.enc_code1 == "undefined") {
      totalImageCount = mh_info.totalimg;
    } else {
      totalImageCount = aes.decrypt(base64Decode(mh_info.enc_code1), "enc_code1");
    }
    //服务器选择 和路线
    let servers = [mh_info.domain], server;
    try {
      eval(src.match(/(var __JS_READLINE[\s\S]+?)<\/script>/)[1]);
      servers.push(__JS_READLINE.domain || __JS_READLINE.data[0].domain);
    } catch (e) {}
    server = mh_info.use_server == "" ? servers[0] : servers[0].replace(/^img/, `img${mh_info.use_server}`);
    //请求头和图片二次加密
    let options ={}, suffix = "jpg"
    if (image_info.imgKey != undefined && image_info.imgKey != '') {
      //临时options
      options = {
      	  "key": enc_webp_bytes_key = aes.decrypt(image_info.imgKey, "C_DATA"),
        "headers": {
          "Cookie": `_tka_${mh_info.pageid}=${path};_tkb_${mh_info.pageid}=${totalImageCount}`,
          "Origin":`${source.getKey()}`
        }
      };
      suffix = "enc.webp";
    }
    if (DEBUG) totalImageCount = 1
    for (;mh_info.startimg <= totalImageCount;) {
      let url = `https://${server}/comic/${encodeURI(path)}${String(mh_info.startimg++).padStart(4,"0")}.${suffix}`;
      html += url.toImg(null, options) + "\n"
    }
  } else {
    //直链类型
    let urls__direct = base64Decode(image_info.urls__direct).split("|SEPARATER|");
    if (DEBUG) urls__direct.splice(0,1);
    if (image_info.img_type == 1) {
      html = urls__direct.toImg(url => url + ".webp");
    } else {
      html = urls__direct.toImg()
    }
  }
  return html
}

(function(){

if (local_keys!=null) {
//固定密钥
aes.keys=local_keys;
}else if (source.getVariable()!=null){
//保存的密钥
  aes.saveKeys(source.getVariable());
} else {
//远程密钥
!DEBUG && aes.saveKeys(fetchRemoteKeys());
aes.retryNum = 1;

DEBUG && aes.saveKeys(parseKeys())
}

return decodeImage(src);

}())

